        int iThuThach2 = Math.abs(tmpDay - tmpYear);
        int iThuThach3 = Math.abs(iThuThach1 - iThuThach2);
        int iThuThach4 = Math.abs(tmpMonth - tmpYear);

        txtHeaders[3].setText("Các giai đoạn thử thách: " + iThuThach1 + " - " + iThuThach2 + " - " + iThuThach3 + " - " + iThuThach4);
