    private static String taoChuoi(String stringName) {
        String mangChuoi = "";
        for (int i = 0; i < stringName.length(); i++) {
            switch (stringName.charAt(i)) {
                case 'A':
                case 'J':
                case 'S':
                    mangChuoi += "1";
                    break;
                case 'B':
                case 'K':
                case 'T':
                    mangChuoi += "2";
                    break;
                case 'C':
                case 'L':
                case 'U':
                    mangChuoi += "3";
                    break;
                case 'D':
                case 'M':
                case 'V':
                    mangChuoi += "4";
                    break;
                case 'E':
                case 'N':
                case 'W':
                    mangChuoi += "5";
                    break;
                case 'F':
                case 'O':
                case 'X':
                    mangChuoi += "6";
                    break;
                case 'G':
                case 'P':
                case 'Y':
                    mangChuoi += "7";
                    break;
                case 'H':
                case 'Q':
                case 'Z':
                    mangChuoi += "8";
                    break;
                case 'I':
                case 'R':
                    mangChuoi += "9";
                    break;
                default:
                    break;
            }
        }
        return mangChuoi;
    }

    public static String sumDigitsToOne(String str) {
        while (str.length() > 1) {
            int sum = 0;
            for (int i = 0; i < str.length(); i++) {
                sum += Character.getNumericValue(str.charAt(i));
            }
            str = Integer.toString(sum);
        }
        return str;
    }
